import { useState } from "react";

import Tab from "./Tab/Tab";

import './Filter.css';

const Filter = ({ data }) => {
    const [active, setActive] = useState(1);

    const selectTabHandler = (key) => {
        setActive(key);
    };

    return (
        <div className="filter-container">
            {data.map((data) => (
                <Tab key={data.id} name={data.name} active={active} onSelectTab={selectTabHandler} />
            ))}
        </div>
    )
};

export default Filter;
